input = ''
test_input = 'zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'


def get_marker(signal, distinct_chars):
    marker = ''
    for i in range(0, len(signal)):
        for y in range(i , len(signal) - 1):
            if len(marker) == distinct_chars:
                return marker, y + 1
            if signal[y + 1] not in marker:
                marker += signal[y + 1]
            else:   
                marker = ''
                break
            
print(get_marker(input, 14))





